import React, { useState } from 'react';
import { Image, Button, Spinner } from 'react-bootstrap';
import { base64Encode } from 'react-base64';
import { extractColorsFromImage, calculateColorSimilarity, calculateSimilarityPercentage } from './imageProcessUtils';

const ImageComparison = () => {
  const [image1, setImage1] = useState('');
  const [image2, setImage2] = useState('');
  const [similarityPercentage, setSimilarityPercentage] = useState(0);
  const [isComparing, setIsComparing] = useState(false);

  const handleImage1Upload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = () => {
      const base64Image = reader.result;
      setImage1(base64Image);
    };
    reader.readAsDataURL(file);
  };

  const handleImage2Upload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = () => {
      const base64Image = reader.result;
      setImage2(base64Image);
    };
    reader.readAsDataURL(file);
  };

  const handleCompareImages = async () => {
    try {
      setIsComparing(true);
      const image1Base64Encoded = base64Encode(image1);
      const image2Base64Encoded = base64Encode(image2);

      const colors1 = await extractColorsFromImage(image1Base64Encoded, 5);
      const colors2 = await extractColorsFromImage(image2Base64Encoded, 5);

      const emdDistance = await calculateColorSimilarity(colors1, colors2, 5);
      const maxPossibleDistance = 10.0;
      const similarityPercentage = calculateSimilarityPercentage(emdDistance, maxPossibleDistance);

      setSimilarityPercentage(similarityPercentage);
      setIsComparing(false);
    } catch (error) {
      console.error(error);
      setIsComparing(false);
    }
  };

  return (
    <div className="image-comparison-container">
      <h2>Image Comparison</h2>
      <div className="image-upload-section">
        <label>Image 1:</label>
        <input type="file" onChange={handleImage1Upload} />
        {image1 && (
          <Image src={image1} alt="Image 1" className="uploaded-image" />
        )}
      </div>
      <div className="image-upload-section">
        <label>Image 2:</label>
        <input type="file" onChange={handleImage2Upload} />
        {image2 && (
          <Image src={image2} alt="Image 2" className="uploaded-image" />
        )}
      </div>
      <Button
        variant="primary"
        disabled={isComparing}
        onClick={handleCompareImages}
      >
        Compare Images
      </Button>
      {isComparing && (
        <div className="loading-spinner">
          <Spinner animation="border" role="status" />
          <span>Comparing Images...</span>
        </div>
      )}
      <div className="similarity-percentage">
        <p>Similarity Percentage: {similarityPercentage}%</p>
      </div>
    </div>
  );
};

export default ImageComparison;
