import React, { useState } from "react";
// import "./styles.css"; // Assuming you have the necessary CSS for styling

const countries = [
  "Anguilla",
  "Aruba",
  "Austria",
  "Azerbaijan",
  "Belgium",
  "Bulgaria",
  "Croatia",
  "Curacao",
  "Cyprus",
  "Denmark",
  "Finland",
  "France",
  "Georgia",
  "Germany",
  "Greece",
  "Italy",
  "Luxembourg",
  "Malta",
  "Monaco",
  "Netherlands",
  "Netherlands Antilles",
  "Norway",
  "Poland",
  "Portugal",
  "Romania",
  "Spain",
  "Sweden",
  "Switzerland",
  "Ukraine",
  "United Kingdom",
];

const Select = () => {
  const [selectedCountry, setSelectedCountry] = useState("");
  const [searchedText, setSearchedText] = useState("");
  const [isActive, setIsActive] = useState(false);

  const addCountry = (selectedLang) => {
    return countries.map((country) => (
      <li
        key={country}
        onClick={() => updateName(country)}
        className={country === selectedLang ? "selected" : ""}
      >
        {country}
      </li>
    ));
  };

  const updateName = (selectedCountry) => {
    setSearchedText("");
    setSelectedCountry(selectedCountry);
    setIsActive(false);
  };

  const handleInputChange = (e) => {
    setSearchedText(e.target.value.toLowerCase());
  };

  const filteredCountries = countries.filter((country) =>
    country.toLowerCase().startsWith(searchedText)
  );

  return (
    <div className="select-wrapper">
      <div className="select-btn" onClick={() => setIsActive(!isActive)}>
        <span>{selectedCountry ? selectedCountry : "Select a country"}</span>
        <ion-icon name="chevron-down"></ion-icon>
      </div>
      <div className={`content ${isActive ? "active" : ""}`}>
        <div className="search">
          <ion-icon name="search"></ion-icon>
          <input
            type="text"
            value={searchedText}
            onChange={handleInputChange}
            placeholder="Search"
          />
        </div>
        <ul className="options">
          {filteredCountries.length > 0 ? (
            addCountry(selectedCountry)
          ) : (
            <p>Lang not found</p>
          )}
        </ul>
      </div>
    </div>
  );
};

export default Select;
